/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fn_tinh_gia`(
	p_ma_dich_vu varchar(30),
	p_ma_tinh_di varchar(30),
	p_ma_tinh_den varchar(30),
	p_khoi_luong int,
