	) RETURNS decimal(20,0)
    DETERMINISTIC
BEGIN
	DECLARE d_gia DECIMAL(20,0);
	DECLARE d_co_vuot_khoi_luong INT;	
	DECLARE d_don_vi_khoi_luong_vuot INT;
