/*Table structure for table `dev_posts` */

DROP TABLE IF EXISTS `dev_posts`;

CREATE TABLE `dev_posts` (
  `ID` bigint(20) unsigned NOT NULL auto_increment,
  `post_author` bigint(20) unsigned NOT NULL default '0',
  `post_date` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL default 'publish',
  `comment_status` varchar(20) NOT NULL default 'open',
  `ping_status` varchar(20) NOT NULL default 'open',
  `post_password` varchar(20) NOT NULL default '',
  `post_name` varchar(200) NOT NULL default '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL default '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL default '0',
  `guid` varchar(255) NOT NULL default '',
  `menu_order` int(11) NOT NULL default '0',
  `post_type` varchar(20) NOT NULL default 'post',
  `post_mime_type` varchar(100) NOT NULL default '',
  `comment_count` bigint(20) NOT NULL default '0',
  PRIMARY KEY  (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=772 DEFAULT CHARSET=utf8;

/*Data for the table `dev_posts` */

insert  into `dev_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2012-06-17 17:34:55','2012-06-17 17:34:55','<table width=\"561\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\">Là dịch vụ vận chuyển thư từ, hàng hóa trong nước từ TP HCM hoặc TP Hà Nội đi tất cả các 64 tỉnh thành trong cả nước.\r\nDịch vụ này có thời gian quy định cụ thể 6h-&gt;72h tùy theo khu vực phát.</td>\r\n<td width=\"30\"><img src=\"http://www.aa-express.net/images/stories/fan2011990.jpg\" alt=\"\" width=\"150\" height=\"116\" border=\"0\" /></td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">&nbsp;\r\n\r\nDịch vụ phát trước 9h sáng\r\n\r\nLà dịch vụ rất tiện lợi khi khách hàng gửi tài liệu hàng hóa trước 17h mà yêu cầu A&amp;A phát trước 9h sáng hôm sau áp dụng trung tâm các tỉnh như: Hà Nội, Đà Nẵng, Cần Thơ, Nha Trang (Dịch vụ này có cộng thêm phí).</td>\r\n<td width=\"30\"><img src=\"http://www.aa-express.net/images/stories/A350.jpg\" alt=\"\" width=\"150\" height=\"100\" align=\"left\" border=\"0\" /></td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Dịch vụ điện hoa\r\n\r\n<strong>K</strong>hi khách hàng cần tặng hoa, quà đến người thân, cơ quan tổ chức, hội nghị liên hoan, khai trương, ma chay ... A&amp;A sẽ phục vụ Quý Khách được nhu cầu này.</td>\r\n<td width=\"30\"><img src=\"http://www.aa-express.net/images/stories/bld009124.jpg\" alt=\"\" width=\"150\" height=\"100\" border=\"0\" /></td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Dịch vụ chuyển phát nhanh hỏa tốc. (Phát trong ngày)\r\n\r\nĐây là dịch vụ mà khi khách hàng có nhu cầu chuyển gấp tài liệu, hàng hóa, hồ sơ đi từ <strong>TP HCM</strong> đi <strong>TP Hà Nội</strong>, <strong>Đà Nẵng</strong>,<strong>Hải Phòng</strong>, <strong>Hà Đông</strong>, <strong>Cần Thơ</strong>, <strong>Vũng Tàu</strong> ... để đến được trong ngày quý khách vui lòng sử dụng dịch vụ phát trong ngày (PTN).\r\nThời gian nhận hàng trước <strong>11h30</strong> thời gian phát hàng muộn nhất là <strong>19h30</strong> cùng ngày. (Dịch vụ này có cộng thêm cước phí).</td>\r\n<td width=\"30\"> <img src=\"http://www.aa-express.net/images/stories/ddl_67a_7162.jpg\" alt=\"\" width=\"150\" height=\"112\" border=\"0\" /></td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"3\" valign=\"top\" width=\"561\">Dịch vụ vận chuyển hàng bằng đường bộ\r\n\r\nKhi khách hàng có hàng chuyển đi mà không cần nhanh thì quý khách vui lòng sử dụng dịch vụ này. Dịch vụ này sẻ tiết kiệm được đáng kể chi phí của khách hàng. Nhưng thời gian giao hàng chậm hơn so với dịch vụ chuyển phát nhanh là 3-4 ngày.</td>\r\n</tr>\r\n</tbody>\r\n</table>','Kiến thức','','publish','open','open','','hello-world','','','2012-06-24 10:12:36','2012-06-24 03:12:36','',0,'http://localhost/wordpress/?p=1',0,'post','',0),(2,1,'2012-06-17 17:34:55','2012-06-17 17:34:55','[gallery]\r\n\r\nThis is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\r\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like piña coladas. (And gettin\' caught in the rain.)</blockquote>\r\n...or something like this:\r\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickies to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\r\nAs a new WordPress user, you should go to <a href=\"http://localhost/wordpress/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','trash','open','open','','sample-page','','','2012-06-23 23:05:44','2012-06-23 16:05:44','',0,'http://localhost/wordpress/?page_id=2',0,'page','',0),(4,1,'2012-06-17 17:58:19','2012-06-17 17:58:19','<table width=\"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n<td valign=\"top\"></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\"><strong>AA Express</strong> là công ty hàng đầu trong lĩnh vực chuyển phát nhanh tài liệu, hàng hóa trong nước và Quốc tế. Nhận từ tay người gửi, phát đến tay người nhận trong thời gian <strong>NGẮN NHẤT</strong>, đảm bảo tuyệt đối <strong>AN TOÀN</strong>,<strong>HIỆU QUẢ</strong>.\r\n\r\nVới đội ngũ nhân viên chuyên nghiệp và năng động, <strong>AA Express</strong> luôn phấn đấu nỗ lực vì lợi ích khách hàng. Hiện nay công ty chúng tôi vẫn và đang hoàn thiện mở rộng quy mô phục vụ và đa dạng hóa các loại hình dịch vụ nhằm đảm bảo chất lượng phục vụ cho mọi khách hàng.<strong>AA Express</strong> sẵn sàng đáp ứng với các phương thức:\r\n<ul>\r\n	<li>Giao nhận hàng tại địa chỉ quý khách</li>\r\n	<li>Không giới hạn về số lượng, trọng lượng.</li>\r\n	<li>Khai thác, quản lý, vận chuyển hàng hóa, tài liệu.</li>\r\n	<li>24/24 trong ngày đến hơn 60 tỉnh thành phố trong cả nước và hơn 200 quốc gia trên thế giới.</li>\r\n</ul>\r\n<strong>Đặc biệt</strong>:\r\n<ul>\r\n	<li>Tra cứu thông tin bưu phẩm thông qua website.</li>\r\n	<li>Tra cứu trọng lượng gói bưu phẩm muốn vận chuyển.</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','Giới thiệu','','publish','open','open','','gioi-thieu-cong-ty-aa-express','','','2012-06-24 09:07:15','2012-06-24 02:07:15','',0,'http://localhost/aaexpress/?page_id=4',0,'page','',0),(5,1,'2012-06-17 17:58:09','2012-06-17 17:58:09','','Gioi thieu cong ty AA-EXPRESS','','inherit','open','open','','4-revision','','','2012-06-17 17:58:09','20