/*!50003 DROP FUNCTION IF EXISTS `fn_tinh_gia` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fn_tinh_gia`(
	p_ma_dich_vu VARCHAR(30),
	p_ma_tinh_di varchar(30),
	p_ma_tinh_den varchar(30),
	p_ma_tinh_den_2 VARCHAR(30),
	p_khoi_luong int,
	p_ma_loai_hang_hoa VARCHAR(30)
	) RETURNS decimal(20,0)
    DETERMINISTIC
BEGIN
	DECLARE d_gia DECIMAL(20,0);
	DECLARE d_gia_toi_da DECIMAL(20,0);
	DECLARE d_co_vuot_khoi_luong INT;	
	DECLARE d_don_vi_khoi_luong_vuot INT;
	DECLARE d_gia_cong_them_gui_sau_12h DECIMAL(20,0);
	DECLARE d_khoi_luong_tu INT;
	
	select gia,gia_toi_da,co_vuot_khoi_luong,don_vi_khoi_luong_vuot,gia_cong_them_gui_sau_12h,khoi_luong_tu
		into d_gia,d_gia_toi_da,d_co_vuot_khoi_luong,d_don_vi_khoi_luong_vuot,d_gia_cong_them_gui_sau_12h,d_khoi_luong_tu
	FROM gia_bang_gia
	WHERE ma_dich_vu=p_ma_dich_vu
		and ma_tinh_di=p_ma_tinh_di 
		and ma_tinh_den=p_ma_tinh_den 
		and p_khoi_luong>=khoi_luong_tu
		and p_khoi_luong<=khoi_luong_den
	limit 1; 
	if d_co_vuot_khoi_luong=1 then
	begin
		DECLARE d_khoi_luong_vuot INT;		
		DECLARE d_gia_tri_vuot DECIMAL(20,0);
		DECLARE d_gia_max_can_tren INT;		
		DECLARE d_khoi_luong_max_can_tren INT;		
		SET d_khoi_luong_max_can_tren = d_khoi_luong_tu - 1;
		SELECT max(gia)
			INTO d_gia_max_can_tren
		FROM gia_bang_gia
		WHERE ma_dich_vu=p_ma_dich_vu
			AND ma_tinh_di=p_ma_tinh_di 
			AND ma_tinh_den=p_ma_tinh_den 
			AND khoi_luong_den<d_khoi_luong_tu;
		SET d_khoi_luong_vuot = p_khoi_luong - d_khoi_luong_max_can_tren;
		SET d_gia_tri_vuot = d_gia_max_can_tren + ceil(d_khoi_luong_vuot / d_don_vi_khoi_luong_vuot) * d_gia;
		
		if d_gia_tri_vuot > d_gia_toi_da then
		begin
			return d_gia_toi_da;
		end;
		end if;			
		RETURN d_gia_tri_vuot;
	end;
	end if;
	return d_gia;
END */$$
DELIMITER ;

